import { Component, OnInit, OnDestroy, Inject } from '@angular/core';
import { User } from 'src/Models/User';
import { UserService } from 'src/app/Services/user.service';
import { USER_TOKEN } from 'src/app/app.module';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-user-detail',
  templateUrl: './user-detail.component.html'
})
export class UserDetailComponent implements OnInit, OnDestroy {
  selectedUser: User;
  private userDetailsSubscription: Subscription;

  constructor(@Inject(USER_TOKEN) private userService: UserService) {}

  ngOnInit() {
    this.userDetailsSubscription = this.userService.onUserDetailsClick.subscribe((data: User) => {
      this.selectedUser = data;

      // Check if the selected user is deleted from the list
      if (!this.userService.users.includes(this.selectedUser)) {
        this.selectedUser = null;
      }
    });
  }

  ngOnDestroy() {
    this.userDetailsSubscription.unsubscribe();
  }
}
<div class="user-detail-container">
    <div>
        <img class="user-detail-avatar-image" src="./assets/images/male-avatar.png">
    </div>
    <div class="user-detail-info">
        <h2>User Detail</h2>
        <p><b>Name:</b>{{selectedUser.name}}</p>
        <p><b>Gender:</b> {{selectedUser.gender}}</p>
        <p><b>Subscription Type:</b> {{selectedUser.subType}}</p>
        <p><b>Status:</b> {{selectedUser.status}}</p>
    </div>    
</div>
